document.addEventListener('DOMContentLoaded', function () {
    const header = document.querySelector('.oxy-header-row.headerRow.new-header-row');

    window.addEventListener('scroll', () => {
        if (window.scrollY > 50) { // Change threshold as needed
            header.classList.add('scrolled');
        } else {
            header.classList.remove('scrolled');
        }
    });
});
	
	document.addEventListener('DOMContentLoaded', function () {
    const tabsWrapper = document.querySelector('.custom-mobile-tab');
    const tabs = document.querySelectorAll('.custom-mobile-tab .oxy-tab');
    const dropdown = document.createElement('select');

    dropdown.className = 'mobile-tabs-dropdown';

    // Create dropdown options based on each tab
    tabs.forEach((tab, index) => {
        const option = document.createElement('option');
        option.value = index;
        option.textContent = tab.textContent; // Use the tab's text as dropdown option
        dropdown.appendChild(option);
    });

    // Insert dropdown above the tabs wrapper
    tabsWrapper.parentNode.insertBefore(dropdown, tabsWrapper);

    // Listen for dropdown change and activate the selected tab
    dropdown.addEventListener('change', function () {
        const selectedIndex = parseInt(this.value, 10);

        // Simulate click on the selected tab to activate
        tabs[selectedIndex].click();
    });
});

document.querySelectorAll('.case-study-card').forEach((card, index) => {
    const openButton = card.querySelector('.open-modal-btn');
    const modal = card.querySelector('.modal-overlay');
    const closeButton = modal.querySelector('.close-btn');

    // Open modal when the button is clicked
    openButton.addEventListener('click', () => {
        modal.classList.remove('hidden'); // Show modal
    });

    // Close modal when the close button is clicked
    closeButton.addEventListener('click', () => {
        modal.classList.add('hidden'); // Hide modal
    });

    // Close modal if clicking outside the content area
    modal.addEventListener('click', (event) => {
        if (event.target === modal) {
            modal.classList.add('hidden');
        }
    });
});

	

document.addEventListener("DOMContentLoaded", function() {
    // Select all submenu links
    const submenuLinks = document.querySelectorAll(".subMenuLinks a");

    // Function to add 'active' class based on current URL
    function setActiveLink() {
        submenuLinks.forEach(link => {
            // Check if the link's href matches the current URL
            if (link.href === window.location.href) {
                link.classList.add("active");
            } else {
                link.classList.remove("active");
            }
        });
    }

    // Run the function to set active link on page load
    setActiveLink();

    // Add click event to update the active class
    submenuLinks.forEach(link => {
        link.addEventListener("click", function() {
            // Remove 'active' class from all links
            submenuLinks.forEach(link => link.classList.remove("active"));

            // Add 'active' class to the clicked link
            this.classList.add("active");
        });
    });
});

	
	
document.addEventListener('DOMContentLoaded', function () {
    function initializeDropdown(wrapperSelector, dropdownSelector) {
        const wrappers = document.querySelectorAll(wrapperSelector);

        wrappers.forEach((wrapper) => {
            const dropdown = wrapper.querySelector(dropdownSelector);

            if (dropdown) {
                // Apply styles to the wrapper and dropdown
                wrapper.style.position = 'relative'; // Ensures wrapper has a relative position

                dropdown.style.display = 'none'; // Initially hide the dropdown
                dropdown.style.position = 'absolute'; // Position dropdown absolutely
                dropdown.style.top = '100%'; // Align directly below the parent
                dropdown.style.left = '0';
                dropdown.style.zIndex = '1000'; // Ensure dropdown appears above other content
                dropdown.style.backgroundColor = '#fff'; // Set dropdown background color
                dropdown.style.border = '1px solid #ddd'; // Border style for the dropdown
                dropdown.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)'; // Box shadow for the dropdown
                dropdown.style.width = 'max-content'; // Adjusts dropdown width

                // Toggle dropdown visibility when the wrapper is clicked
                wrapper.addEventListener('click', function (e) {
                    // Check if dropdown is already visible
                    if (dropdown.style.display === 'block') {
                        dropdown.style.display = 'none'; // Close dropdown if visible
                    } else {
                        dropdown.style.display = 'block'; // Show dropdown if hidden
                    }

                    // Prevent the click event from propagating to the document listener
                    e.stopPropagation();
                });

                // Close the dropdown if clicked outside the wrapper
                document.addEventListener('click', function (e) {
                    if (!wrapper.contains(e.target)) {
                        dropdown.style.display = 'none'; // Close if clicked outside
                    }
                });
            }
        });
    }

    // Call the function with class selectors for wrapper and dropdown
    initializeDropdown('.gtranslate_wrapper', '.gt_option');
});
	
	
	

