document.addEventListener("DOMContentLoaded", function () {
    let containers = document.querySelectorAll(".main-search-people .positionsNames");
    
    containers.forEach(container => {
        let spans = container.querySelectorAll("span");
        
        if (spans.length > 0) {
            let primaryTerm = spans[0].textContent.trim(); // Get the first term
            spans.forEach(span => span.remove()); // Remove all spans
            
            let newSpan = document.createElement("span");
            newSpan.textContent = primaryTerm; // Show only primary term
            container.appendChild(newSpan);
        }
    });
});

